<script type="text/javascript">
     $(document).ready(function () {
         function dicomdolar() {
            $.ajax({
                type: 'GET', //THIS NEEDS TO BE GET
                url: '/dicomdolar/',
                success: function (data) {
                    $('#dicom').html(data);
                }
            });
        }
        function monitordolar() {
            $.ajax({
                type: 'GET', //THIS NEEDS TO BE GET
                url: '/monitordolar/',
                success: function (data) {
                    $('#monitor').html(data);
                }
            });
        }
        $.fn.populateSelect = function (values) {
            var options = '<option value=" ">' + '---' + '</option>';
            //var options = '';
            $.each(values, function (key, row) {
                options += '<option value="' + row.value + '">' + row.text + '</option>';
            });
            $(this).html(options);
        }
        $.fn.populateSelect2 = function (values) {
            var options = '<option value=" ">' + '---' + '</option>';
            options += '<option value="0">' + 'TODOS' + '</option>';
            //var options = '';
            $.each(values, function (key, row) {
                options += '<option value="' + row.value + '">' + row.text + '</option>';
            });
            $(this).html(options);
        }
        $.fn.populateSelect3 = function (values) {
            var options = '<option value=" ">' + '---' + '</option>';
            //var options = '';
            $.each(values, function (key, row) {
                options += '<option value="' + row.value + '">' + row.text + '</option>';
            });
            $(this).html(options);
        }
        $.fn.invisible = function() {
            return this.each(function() {
                $(this).css("visibility", "hidden");
            });
        };
        $.fn.visible = function() {
            return this.each(function() {
                $(this).css("visibility", "visible");
            });
        };
        $('#vmarca').change(function () {
            var id_marca = $(this).val();
            if (id_marca == '') {
                $('#vmodelo').empty().change();
            } else {
                $.getJSON('/vmodelos/'+id_marca, null, function (values) {
                    $('#vmodelo').populateSelect(values);
                });
            }
        });
        // Busca los municipios del estado seleccionado
        $('#estado').change(function () {
            var id_estado = $(this).val();
            if (id_estado == '') {
                $('#municipio').empty().change();
                $('#parroquia').empty().change();
            } else {
                $.getJSON('/municipios/'+id_estado, null, function (values) {
                    $('#municipio').populateSelect(values);
                });
                 $('#parroquia').empty().change();
            }
        });

        $('#banco_id').change(function () {
             var idbanco = $(this).val();
             if (idbanco == "") {
                $('#numero_cuenta').val('');
             }else{
                 $.ajax({
                    type: 'GET', //THIS NEEDS TO BE GET
                    url: '/bancprefijo/'+idbanco,
                    success: function (data) {

                       $('#numero_cuenta').val(data);
                    }
                });
            }
        });

        $('#tingre').change(function () {
           var value = $(this).val();
           if (value=='PAG'){
                $('#factura').show();
           }else{
                $('#factura').hide();
                $('#nrofactura').val('');
           }
           if (value=='NCCC'){
                $('#nccobrar').show();
           }else{
                $('#nccobrar').hide();
                $('#cxcobrar').val('').change();
           }
           if (value=='NCPE'){
                $('#nrofactura').val('');
                $('#cxcobrar').val('').change();
           }
        });

         $('#tiegre').change(function () {
           var value = $(this).val();
           if (value>=1){
                if(value==1){
                    $('#proveedor').show();
                    $('#expediente').hide();
                    $('#pagos').hide();
                    $('#nfactura').show();
                    $('#idpago').val('');
                    $('#cpagar').val('');
                }
                if(value==2){
                    $('#proveedor').hide();
                    $('#expediente').hide();
                    $('#pagos').hide();
                    $('#nfactura').show();
                    $('#idpago').val('');
                    $('#ncpagar').hide();
                }
                if(value==3){
                    $('#proveedor').hide();
                    $('#expediente').hide();
                    $('#pagos').show();
                    $('#nfactura').hide();
                     $('#ncpagar').hide();
                }
                 if(value==4){
                    $('#proveedor').hide();
                    $('#expediente').hide();
                    $('#pagos').hide();
                    $('#nfactura').hide();
                    $('#ncpagar').show();
                }
           }else{
                $('#proveedor').hide();
                $('#expediente').hide();
                $('#pagos').hide();
                $('#nfactura').hide();
                $('#idpago').val('');
                $('#cpagar').val('');
                $('#ncpagar').hide();
           }
        });

        $('#expre').change(function () {
            //event.preventDefault();
            var id = $(this).val();

            if (id == " ") {
                $('#totalpre').val('');
                $('#totalpred').val('');
                $('#tasac').val('');
            }else{
                 $.ajax({
                    type: 'GET', //THIS NEEDS TO BE GET
                    url: '/precoti/'+id,
                    success: function (data) {
                        var totalpre=0;
                        var totalpred=0;
                        totalpre=parseFloat(data.total_bs)+parseFloat(data.total_iva_bs);
                        totalpred=parseFloat(data.total_divisa)+parseFloat(data.total_iva_divisa);
                        $('#totalpre').val(totalpre.toFixed(2));
                        $('#totalpred').val(totalpred.toFixed(2));
                        $('#tasac').val(parseFloat(data.pvp_tasa_cotizacion).toFixed(2));
                    }
                });
            }
        });

        $("#mopagob").keydown(function() {
            var tcambio=$('#tasac').val();
            var valor=$(this).val();
            if((parseFloat(tcambio)>0)||(tcambio!='')){
                 $('#mopagod').val(parseFloat(valor/tcambio).toFixed(2));
            }
        }).keydown();

        $("#mopagod").keydown(function() {
            var tcambio=$('#tasac').val();
            var valor=$(this).val();
            if((parseFloat(tcambio)>0)||(tcambio!='')){
                 $('#mopagob').val(parseFloat(valor*tcambio).toFixed(2));
            }
        }).keydown();

        $("#nrofactura").keyup(function() {
            var valor=$(this).val();
            if (valor==''){
                $('#mopagob').val('');
                $('#mopagod').val('');
                $('#tasac').val('');
                //alert('vacio');
            }else{
                 $('#doltip').val('99').change();
                $.ajax({
                    type: 'GET', //THIS NEEDS TO BE GET
                    url: '/facturas/find/'+valor,
                    success: function (data) {

                        var abonos=0;
                        var cabonos=data.abonos.length;
                        var cantici=data.anticipo.length;
                        alert(cabonos+'-'+cantici);
                        if (cabonos!=0){
                            $.each(data.abonos, function(i, valor) {
                                abonos=parseFloat(abonos+valor.saldo_restante_bs);
                            });
                        }else{
                            if (cantici!=0){
                                $.each(data.anticipo, function(i, valor) {
                                    abonos=parseFloat(abonos+(valor.saldo_total_bs-valor.saldo_restante_bs));
                                });
                            }
                        }
                         $('#tasac').val(parseFloat(data.factura['pvp_tasa_cotizacion']).toFixed(2));
                         $('#mopagob').val(parseFloat(data.factura['total_factura_bs']-abonos).toFixed(2));
                         $('#mopagod').val(parseFloat((data.factura['total_factura_bs']-abonos)/data.factura['pvp_tasa_cotizacion']).toFixed(2));
                         $('#tasac').val(parseFloat(data.factura['pvp_tasa_cotizacion']).toFixed(2));
                        // alert(data.abonos[0].saldo_total_bs);
                    }
                });
            }

        }).keyup();

        $("#porant").keyup(function() {
            var porcen    =  parseFloat($(this).val()/100);
            var totalpre  =  $('#totalpre').val();
            var totalpred =  $('#totalpred').val();
            $('#mpagob').val(parseFloat(totalpre*porcen).toFixed(2));
            $('#mpagod').val(parseFloat(totalpred*porcen).toFixed(2));
            //alert(porcen);
        }).keyup();

        $("#mpagob").keyup(function() {
            var mpagob    =  parseFloat($(this).val());
            var tasac= $('#tasac').val();
            var tprec=$('#totalpre').val();
            $('#mpagod').val(parseFloat(mpagob/tasac).toFixed(2));
            $('#porant').val(parseFloat((mpagob*100)/tprec).toFixed(2));
            //alert(porcen);
        }).keyup();

        $("#mpagod").keyup(function() {
            var mpagod    =  parseFloat($(this).val());
            var tasac= $('#tasac').val();
            var tprec=$('#totalpred').val();
            $('#mpagob').val(parseFloat(mpagod*tasac).toFixed(2));
            $('#porant').val(parseFloat((mpagod*100)/tprec).toFixed(2));
            //alert(porcen);
        }).keyup();

        $("#rif_cliente").keypress(function(e) {
            event.preventDefault();
           var code = (e.keyCode ? e.keyCode : e.which);
           if(code==13){
              var id = $("#cliente_id").val();
              if (id == '') {
                    $('#expre').empty().change();
               } else {
                    $.getJSON('/exprecoti/'+id, null, function (values) {
                        $('#expre').populateSelect(values);
                    });
                }
            }
        });

        $('#bancos').change(function () {
              var id = $(this).val();
              if (id == '') {
                    $('#cuentas').empty().change();
               } else {
                    $.getJSON('/ctabanco/'+id, null, function (values) {
                        $('#cuentas').populateSelect(values);
                    });
                }
        });

         $('#tipo_expediente').change(function () {
              var id = $(this).val();
              if (id == 2) {
                    $('#expodetails').show();
               } else {
                    $('#expodetails').hide();
                }
        });

        $('#retfacturaiva').change(function () {
            var idfac = $(this).val();
            if (idfac == 0) {
                $('#nombre_cliente').val('');
                $('#clienid').val('');
                $('#ivafac').val('');
                $('#bsivafact').val('');
                $('#por_reten').val('');
                $('#bsreten').val('');
            } else {
                $.ajax({
                    type: 'GET', //THIS NEEDS TO BE GET
                    url: '/facturas/find/'+idfac,
                    success: function (data) {
                        // alert(data.factura['total_factura_bs']);
                        $('#nombre_cliente').val(data.factura['nombre_cliente']);
                        $('#clienid').val(data.cliente['id']);
                        $('#ivafac').val(data.ivapor);
                        $('#bsivafact').val(parseFloat(data.factura['total_iva_bs']).toFixed(2));
                        $('#por_reten').val(data.cliente['porcentaje_retencion']);
                        $('#bsreten').val(parseFloat((data.factura['total_iva_bs'])*(data.cliente['porcentaje_retencion']/100)).toFixed(2));
                    }
                });
            }
        });

        $('#retfacturaislr').change(function () {
            var idfac = $(this).val();
            if (idfac == 0) {
                $('#nombre_cliente').val('');
                $('#clienid').val('');
                $('#ivafac').val('');
                $('#bsivafact').val('');
                $('#por_reten').val('');
                $('#bsreten').val('');
            } else {
                $.ajax({
                    type: 'GET', //THIS NEEDS TO BE GET
                    url: '/facturas/find/'+idfac,
                    success: function (data) {
                        // alert(data.factura['total_factura_bs']);
                        $('#nombre_cliente').val(data.factura['nombre_cliente']);
                        $('#clienid').val(data.cliente['id']);
                        // $('#ivafac').val(data.ivapor);
                        $('#bsbaseinponible').val(parseFloat(data.factura['total_bimponible_bs']).toFixed(2));
                        $('#por_reten').val(data.cliente['porcentaje_islr']);
                        $('#bsreten').val(parseFloat((data.factura['total_bimponible_bs'])*(data.cliente['porcentaje_islr']/100)).toFixed(2));
                    }
                });
            }
        });

        $('#cxpagar').change(function () {
            var idfac = $(this).val();
            if (idfac == 0) {
                $('#doltip').val('');
                $('#mopagob').val('');
                $('#mopagod').val('');
                $('#tasac').val('');
                $('#tipopago').val('');
                $('#bancos').val('');
            } else {
                $.ajax({
                    type: 'GET', //THIS NEEDS TO BE GET
                    url: '/findcpagar/'+idfac,
                    success: function (data) {
                        // alert(data.factura['total_factura_bs']);
                        //  $('#mopagob').val(parseFloat(valor*tcambio).toFixed(2));
