<script>
    /*
        Procedimientos y funciones de para ser usados en el mdulo de Expedientes
     */
   $("#tipo_expediente").change(function () {
        var cod_abre = $('#acod_abre').val();
            var tipoExpID = $(this).children("option:selected").val();
            var tipoExpStr = "";
            var expYear = new Date().getFullYear().toString().substr(-2);   // ltimos dos dgitos del ao
            var expID = "";
            var expHexCode = "";
            var DIECISIETE = 17;
            var ONCE = 11;
            var expCodigo = "";

            // Establece el ID del tipo de expediente
            if (tipoExpID == 1) {                                           // Importacin
                tipoExpStr = "IMP";
            } else if (tipoExpID == 2) {                                    // Exportacin
                tipoExpStr = "EXP";
            } else if (tipoExpID == 3) {                                    // Otros
                tipoExpStr = "OTR";
            }

            // Captura el cdigo del expediente de manera temporal
            expCodigo = $("#codigo_expediente").val();
            //alert(expCodigo + '-'+ cod_abre + "-" + expYear + "-" + tipoExpStr + "-" + expHexCode);

            // Captura o asigna el cdigo del Expediente segn sea el caso
            expHexCode = (expCodigo.length == DIECISIETE) ? expCodigo.substring(ONCE, DIECISIETE) : "######";

            // Arma el cdigo del Expediente (incluyendo su parte hexadecimal)
            $("#codigo_expediente").val(cod_abre + "-" + expYear + "-" + tipoExpStr + "-" + expHexCode);
    });
    // Busca por nmero de RIF y muestra el nombre del cliente
    $("#rif_cliente").keyup(function() {
        var rif = $(this).val();
        $.ajax({
            type: 'GET',
            url: '/rif_cliente/' + rif,
            success: function (data) {
                $('#razon_social').val(data['nombre_cliente']);
                $('#direccion_fiscal').val(data['direccion']);
                $('#cliente_id').val(data['id']);
            }
        });
         var id = $("#cliente_id").val();
              if (id == '') {
                    $('#expre').empty().change();
               } else {
                    $.getJSON('/exprecoti/'+id, null, function (values) {
                        $('#expre').populateSelect(values);
                    });
                }
    });
</script>
 <script>
  function justNumbers(e)
        {
        var keynum = window.event ? window.event.keyCode : e.which;
        if ((keynum == 8) || (keynum == 46))
        return true;
        return /\d/.test(String.fromCharCode(keynum));
        }
 </script>
